function calcularCombustivel(){var a=parseFloat(document.getElementById("valorEtanol").value),e=parseFloat(document.getElementById("valorGasolina").value);isNaN(a)||isNaN(e)?document.getElementById("resultado").innerHTML="Por favor, digite valores válidos para o Etanol e a Gasolina.":(a=a/e*100,document.getElementById("resultado").innerHTML=a<=70?"Abasteça com Etanol, essa é a melhor opção!":"Abasteça com Gasolina, essa é a melhor opção!")}